import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Icons } from './Icons';
import { COMPANY_INFO } from '../types';

export const Navbar: React.FC = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navLinks = [
    { name: 'Accueil', path: '/' },
    { name: 'Maison Neuve', path: '/maison-neuve' },
    { name: 'Rénovation', path: '/renovation' },
    { name: 'Réalisations', path: '/realisations' },
  ];

  const closeMenu = () => setIsMobileMenuOpen(false);

  return (
    <nav 
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-300 ${
        isScrolled ? 'bg-white/90 backdrop-blur-md shadow-sm py-3' : 'bg-transparent py-5'
      }`}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center">
          {/* Logo */}
          <Link to="/" className="group" onClick={closeMenu}>
            <div className="flex flex-col">
              <span className={`font-display font-bold text-2xl tracking-tight transition-colors ${
                isScrolled ? 'text-primary' : 'text-primary' // Always primary for visibility
              }`}>
                TM BTP
              </span>
              <span className="text-[10px] tracking-[0.2em] text-accent uppercase font-medium">
                Expertise Premium
              </span>
            </div>
          </Link>

          {/* Desktop Menu */}
          <div className="hidden md:flex items-center space-x-8">
            {navLinks.map((link) => (
              <Link
                key={link.path}
                to={link.path}
                className={`text-sm font-medium tracking-wide hover:text-accent transition-colors relative py-1
                  ${location.pathname === link.path ? 'text-accent' : 'text-primary'}
                `}
              >
                {link.name}
                {location.pathname === link.path && (
                  <span className="absolute bottom-0 left-0 w-full h-0.5 bg-accent rounded-full"></span>
                )}
              </Link>
            ))}
            <Link 
              to="/devis"
              className="px-6 py-2.5 bg-primary text-white text-sm font-semibold rounded-full hover:bg-secondary transition-all shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 border border-transparent hover:border-accent/30"
            >
              Demander un devis
            </Link>
          </div>

          {/* Mobile Toggle */}
          <button 
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            className="md:hidden p-2 text-primary hover:text-accent transition-colors"
          >
            {isMobileMenuOpen ? <Icons.Close size={24} /> : <Icons.Menu size={24} />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      <div 
        className={`fixed inset-0 bg-white z-40 transform transition-transform duration-300 ease-in-out md:hidden flex flex-col pt-24 px-6 ${
          isMobileMenuOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        <div className="flex flex-col space-y-6">
          {navLinks.map((link) => (
            <Link
              key={link.path}
              to={link.path}
              onClick={closeMenu}
              className="text-xl font-display font-bold text-primary border-b border-gray-100 pb-4"
            >
              {link.name}
            </Link>
          ))}
          <Link
            to="/devis"
            onClick={closeMenu}
            className="w-full py-4 bg-primary text-white text-center font-bold rounded-xl shadow-lg mt-4"
          >
            Commencer mon projet
          </Link>

          <div className="mt-8 pt-8 border-t border-gray-100">
            <p className="text-sm text-gray-500 mb-2">Contact direct</p>
            <a href={`tel:${COMPANY_INFO.phoneClean}`} className="flex items-center text-primary font-bold text-lg mb-4">
              <Icons.Phone className="w-5 h-5 mr-3 text-accent" />
