import React, { useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import { Icons } from './Icons';
import { Service } from '../types';

interface ServiceCardProps {
  service: Service;
}

export const ServiceCard: React.FC<ServiceCardProps> = ({ service }) => {
  const cardRef = useRef<HTMLDivElement>(null);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    if (!cardRef.current) return;
    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Calculate rotation (-10 to 10 degrees)
    const rotateY = ((x / rect.width) - 0.5) * 10;
    const rotateX = ((y / rect.height) - 0.5) * -10;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotation({ x: 0, y: 0 });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const Icon = Icons[service.icon as keyof typeof Icons] || Icons.Home;

  return (
    <div className="perspective-1000 w-full h-full" style={{ perspective: '1000px' }}>
      <div
        ref={cardRef}
        onMouseMove={handleMouseMove}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        className="w-full h-full bg-white rounded-2xl p-8 border border-gray-100 shadow-lg relative overflow-hidden transition-all duration-200 ease-out group"
        style={{
          transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) scale(${isHovered ? 1.02 : 1})`,
          boxShadow: isHovered 
            ? '0 20px 40px rgba(0,0,0,0.1), 0 0 0 1px #C9A227' 
            : '0 10px 20px rgba(0,0,0,0.05)',
        }}
      >
        {/* Luminous Edge Effect Gradient */}
        <div 
          className={`absolute inset-0 bg-gradient-to-br from-accent/5 via-transparent to-transparent opacity-0 transition-opacity duration-300 ${isHovered ? 'opacity-100' : ''}`} 
        />

        <div className="relative z-10 flex flex-col h-full">
          <div className="w-14 h-14 bg-primary/5 rounded-xl flex items-center justify-center mb-6 text-primary group-hover:bg-primary group-hover:text-white transition-colors duration-300">
            <Icon className="w-7 h-7" />
          </div>

          <h3 className="font-display font-bold text-xl text-primary mb-3">{service.title}</h3>

          <p className="text-gray-600 text-sm leading-relaxed mb-6 flex-grow">
            {service.description}
          </p>

          <ul className="space-y-2 mb-8">
            {service.features.slice(0, 3).map((feature, idx) => (
              <li key={idx} className="flex items-center text-xs text-gray-500 font-medium">
                <div className="w-1.5 h-1.5 rounded-full bg-accent mr-2"></div>
                {feature}
              </li>
            ))}
          </ul>

          <Link 
            to={service.link}
            className="flex items-center text-sm font-bold text-primary hover:text-accent transition-colors mt-auto"
          >
            En savoir plus 
            <Icons.ChevronRight className="w-4 h-4 ml-1" />
          </Link>
        </div>
      </div>
    </div>
  );
};

import React, { useState, useRef, useEffect } from 'react';
import { Icons } from './Icons';

interface BeforeAfterSliderProps {
  beforeImage: string;
  afterImage: string;
  label?: string;
}

export const BeforeAfterSlider: React.FC<BeforeAfterSliderProps> = ({ beforeImage, afterImage, label }) => {
  const [sliderPosition, setSliderPosition] = useState(50);
  const containerRef = useRef<HTMLDivElement>(null);
  const [isDragging, setIsDragging] = useState(false);

  const handleMove = (clientX: number) => {
    if (containerRef.current) {
      const rect = containerRef.current.getBoundingClientRect();
      const x = Math.max(0, Math.min(clientX - rect.left, rect.width));
      const percentage = (x / rect.width) * 100;
      setSliderPosition(percentage);
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (isDragging) handleMove(e.clientX);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    handleMove(e.touches[0].clientX);
  };

  return (
    <div className="relative w-full h-[400px] md:h-[500px] rounded-2xl overflow-hidden cursor-ew-resize group select-none shadow-2xl"
      ref={containerRef}
      onMouseDown={() => setIsDragging(true)}
      onMouseUp={() => setIsDragging(false)}
      onMouseLeave={() => setIsDragging(false)}
      onMouseMove={handleMouseMove}
      onTouchMove={handleTouchMove}
    >
      {/* After Image (Base) */}
      <img 
        src={afterImage} 
        alt="Après rénovation" 
        className="absolute inset-0 w-full h-full object-cover"
        draggable={false}
      />

      <div className="absolute top-4 right-4 bg-white/90 backdrop-blur text-primary text-xs font-bold px-3 py-1 rounded-full z-10">
        APRÈS
      </div>

      {/* Before Image (Clipped) */}
      <div 
        className="absolute inset-0 w-full h-full overflow-hidden"
        style={{ clipPath: `inset(0 ${100 - sliderPosition}% 0 0)` }}
      >
        <img 
          src={beforeImage} 
          alt="Avant rénovation" 
          className="absolute inset-0 w-full h-full object-cover" 
          draggable={false}
        />
        <div className="absolute top-4 left-4 bg-black/60 text-white text-xs font-bold px-3 py-1 rounded-full">
          AVANT
        </div>
      </div>

      {/* Slider Handle */}
      <div 
        className="absolute top-0 bottom-0 w-1 bg-white cursor-ew-resize z-20 shadow-[0_0_10px_rgba(0,0,0,0.5)]"
        style={{ left: `${sliderPosition}%` }}
      >
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-10 h-10 bg-white rounded-full flex items-center justify-center shadow-lg text-primary">
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <path d="M18 8L22 12L18 16" />
            <path d="M6 8L2 12L6 16" />
          </svg>
        </div>
      </div>

      {label && (
        <div className="absolute bottom-6 left-6 right-6 z-30 pointer-events-none">
          <div className="bg-white/90 backdrop-blur-md p-4 rounded-xl shadow-lg inline-block">
            <h4 className="font-bold text-primary">{label}</h4>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useState } from 'react';
import { Icons } from './Icons';
import { ProjectType, QuoteFormData } from '../types';

export const QuoteWizard: React.FC = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState<QuoteFormData>({
    projectType: null,
    location: '',
    surface: '',
    budget: '',
    name: '',
    phone: '',
    email: '',
    details: ''
  });

  const handleNext = () => setStep(step + 1);
  const handleBack = () => setStep(step - 1);

  const updateField = (field: keyof QuoteFormData, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const projectTypes = [
    { type: ProjectType.NEW_BUILD, icon: Icons.Home, desc: "Construction neuve clé en main" },
    { type: ProjectType.RENOVATION, icon: Icons.Hammer, desc: "Rénovation intérieure/extérieure" },
    { type: ProjectType.PHOTOVOLTAIC, icon: Icons.Electric, desc: "Installation solaire" },
    { type: ProjectType.HVAC, icon: Icons.HVAC, desc: "Pompe à chaleur / Clim" },
  ];

  const generateMessage = () => {
    return `Bonjour TM BTP, je souhaite un devis pour :
- Projet: ${formData.projectType}
- Lieu: ${formData.location}
- Surface: ${formData.surface}
- Budget: ${formData.budget}
- Nom: ${formData.name}
- Détails: ${formData.details}`;
  };

  const whatsappLink = `https://wa.me/33626538445?text=${encodeURIComponent(generateMessage())}`;
  const mailtoLink = `mailto:tmbtp.contact@gmail.com?subject=Demande de devis - ${formData.name}&body=${encodeURIComponent(generateMessage())}`;

  return (
    <div className="bg-white rounded-2xl shadow-xl border border-gray-100 overflow-hidden max-w-4xl mx-auto">
      {/* Progress Bar */}
      <div className="bg-gray-50 px-8 py-4 border-b border-gray-100 flex justify-between items-center">
        <div className="flex space-x-2">
          {[1, 2, 3, 4].map(i => (
            <div key={i} className={`h-2 w-12 rounded-full transition-all ${step >= i ? 'bg-accent' : 'bg-gray-200'}`} />
          ))}
        </div>
        <span className="text-xs font-bold text-gray-400 uppercase tracking-widest">Étape {step} / 4</span>
      </div>

      <div className="p-8 md:p-12 min-h-[400px] flex flex-col">
        {step === 1 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-display font-bold text-primary mb-2">Quel est votre type de projet ?</h2>
            <p className="text-gray-500 mb-8">Sélectionnez la catégorie principale.</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {projectTypes.map((pt) => (
                <button
                  key={pt.type}
                  onClick={() => { updateField('projectType', pt.type); handleNext(); }}
                  className={`p-6 rounded-xl border-2 text-left transition-all hover:shadow-lg flex items-start ${
                    formData.projectType === pt.type 
                      ? 'border-accent bg-accent/5' 
                      : 'border-gray-100 hover:border-accent/50'
                  }`}
                >
                  <div className={`p-3 rounded-full mr-4 ${formData.projectType === pt.type ? 'bg-accent text-white' : 'bg-gray-100 text-gray-500'}`}>
                    <pt.icon size={24} />
                  </div>
                  <div>
                    <h3 className="font-bold text-primary">{pt.type}</h3>
                    <p className="text-sm text-gray-500 mt-1">{pt.desc}</p>
                  </div>
                </button>
              ))}
            </div>
          </div>
        )}

        {step === 2 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-display font-bold text-primary mb-6">Détails du chantier</h2>
            <div className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Ville / Code Postal</label>
                <input 
                  type="text" 
                  className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none"
                  placeholder="ex: Tarbes, 65000"
                  value={formData.location}
                  onChange={(e) => updateField('location', e.target.value)}
                />
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Surface estimée (m²)</label>
                  <input 
                    type="text" 
                    className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none"
                    placeholder="ex: 120"
                    value={formData.surface}
                    onChange={(e) => updateField('surface', e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Budget envisagé</label>
                  <select 
                    className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none bg-white"
                    value={formData.budget}
                    onChange={(e) => updateField('budget', e.target.value)}
                  >
                    <option value="">Sélectionner une fourchette</option>
                    <option value="< 50k">Moins de 50k€</option>
                    <option value="50k-100k">50k€ - 100k€</option>
                    <option value="100k-200k">100k€ - 200k€</option>
                    <option value="> 200k">Plus de 200k€</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="mt-8 flex justify-end space-x-4">
              <button onClick={handleBack} className="px-6 py-3 text-gray-500 font-medium hover:text-primary">Retour</button>
              <button 
                onClick={handleNext} 
                disabled={!formData.location}
                className="px-8 py-3 bg-primary text-white rounded-full font-bold hover:bg-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Suivant
              </button>
            </div>
          </div>
        )}

        {step === 3 && (
          <div className="animate-fade-in">
            <h2 className="text-2xl font-display font-bold text-primary mb-6">Vos coordonnées</h2>
            <div className="grid grid-cols-1 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Nom complet</label>
                <input 
                  type="text" 
                  className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none"
                  value={formData.name}
                  onChange={(e) => updateField('name', e.target.value)}
                />
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Téléphone</label>
                  <input 
                    type="tel" 
                    className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none"
                    value={formData.phone}
                    onChange={(e) => updateField('phone', e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
                  <input 
                    type="email" 
                    className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none"
                    value={formData.email}
                    onChange={(e) => updateField('email', e.target.value)}
                  />
                </div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Message (facultatif)</label>
                <textarea 
                  className="w-full p-4 border border-gray-200 rounded-xl focus:ring-2 focus:ring-accent focus:border-transparent outline-none h-32 resize-none"
                  placeholder="Précisions supplémentaires..."
                  value={formData.details}
                  onChange={(e) => updateField('details', e.target.value)}
                />
              </div>
            </div>
            <div className="mt-8 flex justify-end space-x-4">
              <button onClick={handleBack} className="px-6 py-3 text-gray-500 font-medium hover:text-primary">Retour</button>
              <button 
                onClick={handleNext}
                disabled={!formData.name || !formData.phone}
                className="px-8 py-3 bg-primary text-white rounded-full font-bold hover:bg-secondary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Voir le résumé
              </button>
            </div>
          </div>
        )}

        {step === 4 && (
          <div className="animate-fade-in text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <Icons.Check className="w-8 h-8 text-green-600" />
            </div>
            <h2 className="text-2xl font-display font-bold text-primary mb-2">Tout est prêt !</h2>
            <p className="text-gray-500 mb-8 max-w-md mx-auto">
              Choisissez votre méthode préférée pour nous envoyer votre demande. Nous vous répondrons sous 24h ouvrées.
            </p>

            <div className="bg-gray-50 p-6 rounded-xl text-left mb-8 border border-gray-100 max-w-lg mx-auto">
              <h4 className="font-bold text-gray-900 mb-2">Résumé :</h4>
              <ul className="text-sm text-gray-600 space-y-1">
                <li><span className="font-medium">Type:</span> {formData.projectType}</li>
                <li><span className="font-medium">Lieu:</span> {formData.location}</li>
                <li><span className="font-medium">Client:</span> {formData.name}</li>
              </ul>
            </div>

            <div className="flex flex-col md:flex-row gap-4 justify-center">
              <a 
                href={mailtoLink}
                className="inline-flex items-center justify-center px-8 py-4 bg-primary text-white rounded-xl font-bold hover:bg-secondary transition-all shadow-lg hover:shadow-xl"
              >
                <Icons.Mail className="mr-2 w-5 h-5" />
                Envoyer par Email
              </a>
              <a 
                href={whatsappLink}
                target="_blank"
                rel="noreferrer"
                className="inline-flex items-center justify-center px-8 py-4 bg-[#25D366] text-white rounded-xl font-bold hover:bg-[#20bd5a] transition-all shadow-lg hover:shadow-xl"
              >
                <Icons.Mobile className="mr-2 w-5 h-5" />
                Via WhatsApp
              </a>
            </div>
            <p className="mt-6 text-xs text-gray-400">
              En cliquant, vous ouvrez votre application de messagerie avec le résumé pré-rempli.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

