import React, { useState, useRef, useEffect } from 'react';
import { Icons } from './Icons';

export const InteractiveAreaMap: React.FC = () => {
  const [scale, setScale] = useState(1);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const containerRef = useRef<HTMLDivElement>(null);

  const handleZoomIn = () => setScale(prev => Math.min(prev + 0.5, 4));
  const handleZoomOut = () => setScale(prev => Math.max(prev - 0.5, 1));

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    setDragStart({ x: e.clientX - position.x, y: e.clientY - position.y });
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  };

  const handleMouseUp = () => setIsDragging(false);

  // Reset position if zoomed out completely
  useEffect(() => {
    if (scale === 1) setPosition({ x: 0, y: 0 });
  }, [scale]);

  return (
    <div className="w-full h-[600px] bg-primary relative overflow-hidden group cursor-move select-none rounded-2xl border border-white/10 shadow-2xl">
      {/* UI Controls */}
      <div className="absolute top-6 left-6 z-20 bg-white/10 backdrop-blur-md p-4 rounded-xl border border-white/20">
        <h3 className="text-white font-display font-bold text-lg">Zone d'intervention</h3>
        <p className="text-gray-300 text-xs mt-1">Tarbes — Montauban</p>
        <div className="flex items-center gap-2 mt-2">
            <span className="w-2 h-2 rounded-full bg-green-500 animate-pulse"></span>
            <span className="text-accent text-xs font-bold uppercase tracking-wider">Déplacement possible</span>
        </div>
      </div>

      <div className="absolute bottom-6 right-6 z-20 flex flex-col gap-2">
        <button onClick={handleZoomIn} className="p-3 bg-white text-primary rounded-full hover:bg-accent hover:text-white transition-colors shadow-lg">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M12 5v14M5 12h14"/></svg>
        </button>
        <button onClick={handleZoomOut} className="p-3 bg-white text-primary rounded-full hover:bg-accent hover:text-white transition-colors shadow-lg">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2"><path d="M5 12h14"/></svg>
        </button>
      </div>

      {/* Map Container */}
      <div 
        ref={containerRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        className="w-full h-full flex items-center justify-center transition-transform duration-100 ease-out"
        style={{ 
          transform: `scale(${scale}) translate(${position.x / scale}px, ${position.y / scale}px)` 
        }}
      >
        {/* Stylized SVG Map */}
        <svg width="800" height="600" viewBox="0 0 800 600" className="opacity-90">
          <defs>
            <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
              <path d="M 40 0 L 0 0 0 40" fill="none" stroke="white" strokeWidth="0.5" opacity="0.1"/>
            </pattern>
            <linearGradient id="routeGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#C9A227" stopOpacity="0.8" />
              <stop offset="100%" stopColor="#C9A227" stopOpacity="0.2" />
            </linearGradient>
            <filter id="glow">
              <feGaussianBlur stdDeviation="2.5" result="coloredBlur"/>
              <feMerge><feMergeNode in="coloredBlur"/><feMergeNode in="SourceGraphic"/></feMerge>
            </filter>
          </defs>

          {/* Background Grid */}
          <rect width="100%" height="100%" fill="url(#grid)" />

          {/* Department outlines (Abstract) */}
          <path d="M150,550 Q250,500 300,450 T450,300 T600,150" fill="none" stroke="white" strokeWidth="1" strokeDasharray="5,5" opacity="0.2" />

          {/* The Intervention Zone (Polygon) */}
          <path 
            d="M200,480 L250,520 L550,250 L500,200 Z" 
            fill="rgba(201, 162, 39, 0.05)" 
            stroke="#C9A227" 
            strokeWidth="1"
            className="animate-pulse"
            style={{ animationDuration: '4s' }}
          />

          {/* Main Route Line */}
          <line x1="220" y1="500" x2="520" y2="220" stroke="url(#routeGradient)" strokeWidth="3" strokeLinecap="round" />

          {/* City: Tarbes */}
