import React, { useRef, useState } from 'react';
import { Link } from 'react-router-dom';
import { Icons } from './Icons';
import { Service } from '../types';

interface ServiceCardProps {
  service: Service;
  onClick?: (e: React.MouseEvent) => void;
}

export const ServiceCard: React.FC<ServiceCardProps> = ({ service, onClick }) => {
  const cardRef = useRef<HTMLDivElement>(null);
  const [rotation, setRotation] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    if (!cardRef.current || window.innerWidth < 1024) return; // Disable 3D tilt on mobile
    const rect = cardRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Calculate rotation (-10 to 10 degrees)
    const rotateY = ((x / rect.width) - 0.5) * 10;
    const rotateX = ((y / rect.height) - 0.5) * -10;

    setRotation({ x: rotateX, y: rotateY });
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotation({ x: 0, y: 0 });
  };

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleClick = (e: React.MouseEvent) => {
    if (onClick) {
      e.preventDefault();
      onClick(e);
    }
  };

  // Default to NewBuild (Home) if icon key is not found
  const Icon = Icons[service.icon as keyof typeof Icons] || Icons.NewBuild;

  return (
    <div className="perspective-1000 w-full h-full" style={{ perspective: '1000px' }}>
      <Link
        to={service.link}
        onClick={handleClick}
        className="block h-full"
      >
        <div
          ref={cardRef}
          onMouseMove={handleMouseMove}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          className="w-full h-full bg-white dark:bg-neutral-900 rounded-2xl p-4 md:p-8 border border-gray-100 dark:border-neutral-800 shadow-lg relative overflow-hidden transition-all duration-200 ease-out group hover:border-accent/50 dark:hover:border-accent/50"
          style={{
            transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) scale(${isHovered ? 1.02 : 1})`,
            boxShadow: isHovered 
              ? '0 20px 40px rgba(0,0,0,0.1), 0 0 0 1px #C9A227' 
              : '0 10px 20px rgba(0,0,0,0.05)',
          }}
        >
          {/* Luminous Edge Effect Gradient */}
          <div 
            className={`absolute inset-0 bg-gradient-to-br from-accent/5 via-transparent to-transparent opacity-0 transition-opacity duration-300 ${isHovered ? 'opacity-100' : ''}`} 
          />

          <div className="relative z-10 flex flex-col h-full items-center md:items-start text-center md:text-left">
            <div className="w-12 h-12 md:w-14 md:h-14 bg-primary/5 dark:bg-white/5 rounded-xl flex items-center justify-center mb-4 md:mb-6 text-primary dark:text-accent group-hover:bg-primary dark:group-hover:bg-accent group-hover:text-white dark:group-hover:text-primary transition-colors duration-300">
              <Icon className="w-6 h-6 md:w-7 md:h-7" />
            </div>

            <h3 className="font-display font-bold text-sm md:text-xl text-primary dark:text-white mb-2 md:mb-3 leading-tight">{service.title}</h3>

            <p className="hidden md:block text-gray-600 dark:text-gray-400 text-sm leading-relaxed mb-6 flex-grow">
              {service.description}
            </p>

            <ul className="hidden md:block space-y-2 mb-8 w-full">
              {service.features.slice(0, 3).map((feature, idx) => (
                <li key={idx} className="flex items-center text-xs text-gray-500 dark:text-gray-500 font-medium">
                  <div className="w-1.5 h-1.5 rounded-full bg-accent mr-2"></div>
                  {feature}
                </li>
              ))}
            </ul>

            <div 
              className="flex items-center text-xs md:text-sm font-bold text-primary dark:text-accent group-hover:text-accent dark:group-hover:text-white transition-colors mt-auto"
            >
              <span className="md:hidden">Voir</span>
              <span className="hidden md:inline">En savoir plus</span>
              <Icons.ChevronRight className="w-3 h-3 md:w-4 md:h-4 ml-1" />
            </div>
          </div>
        </div>
      </Link>
    </div>
  );
};

import React, { useEffect } from 'react';
import { Icons } from './Icons';
import { MaisonNeuve } from '../pages/MaisonNeuve';
import { Renovation } from '../pages/Renovation';
import { Photovoltaique } from '../pages/Photovoltaique';
import { Climatisation } from '../pages/Climatisation';
import { Service } from '../types';

interface ServiceModalProps {
  isOpen: boolean;
  serviceId: string;
  onClose: () => void;
  onSwitch: (id: string) => void;
  services: Service[];
}

export const ServiceModal: React.FC<ServiceModalProps> = ({ isOpen, serviceId, onClose, onSwitch, services }) => {
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isOpen]);

  if (!isOpen) return null;

  const renderContent = () => {
    switch (serviceId) {
      case 'maison-neuve': return <MaisonNeuve />;
      case 'renovation': return <Renovation />;
      case 'photovoltaique': return <Photovoltaique />;
      case 'clim': return <Climatisation />;
      default: return <MaisonNeuve />;
    }
  };

  const currentServiceIndex = services.findIndex(s => s.id === serviceId);
  const nextService = services[(currentServiceIndex + 1) % services.length];

  return (
    <div className="fixed inset-0 z-[60] bg-white dark:bg-neutral-950 flex flex-col animate-fade-in">
      {/* Modal Header */}
      <div className="absolute top-0 left-0 w-full h-20 bg-white/90 dark:bg-neutral-950/90 backdrop-blur-md z-50 flex items-center justify-between px-4 border-b border-gray-100 dark:border-neutral-800 shadow-sm">
        <div className="flex items-center">
            <span className="font-display font-bold text-primary dark:text-white text-lg">
                TM BTP <span className="text-accent text-sm ml-2 font-normal hidden sm:inline">Expertise</span>
            </span>
        </div>
        <button 
          onClick={onClose}
          className="w-10 h-10 bg-gray-100 dark:bg-neutral-800 rounded-full flex items-center justify-center text-primary dark:text-white hover:bg-accent hover:text-white transition-colors"
        >
          <Icons.Close className="w-5 h-5" />
        </button>
      </div>

      {/* Content Container (Scrollable) */}
      <div className="flex-grow overflow-y-auto overflow-x-hidden bg-white dark:bg-neutral-950 relative">
        {/* We use a negative margin top to pull the content up under the transparent header if needed, 
            but pages have pt-20. Since our header is fixed, pt-20 works perfectly to spacing. */}
        <div className="min-h-screen">
            {renderContent()}
        </div>

        {/* Footer spacer */}
        <div className="h-24"></div>
      </div>

      {/* Floating Bottom Nav */}
      <div className="absolute bottom-6 left-0 w-full z-50 px-4 flex justify-center pointer-events-none">
        <div className="bg-primary/90 dark:bg-neutral-800/90 backdrop-blur-md text-white px-6 py-3 rounded-full shadow-2xl flex items-center gap-4 pointer-events-auto border border-white/10 max-w-sm w-full justify-between">
           <span className="text-xs font-medium text-gray-300 uppercase tracking-wider">Expertise suivante</span>
           <button 
             onClick={() => {
                const scrollContainer = document.querySelector('.overflow-y-auto');
                if(scrollContainer) scrollContainer.scrollTop = 0;
                onSwitch(nextService.id);
             }}
             className="flex items-center font-bold text-sm hover:text-accent transition-colors"
           >
              {nextService.title}
              <Icons.ChevronRight className="w-4 h-4 ml-1" />
           </button>
        </div>
      </div>
    </div>
  );
};

import React, { useEffect } from 'react';
import { Icons } from './Icons';
import { MaisonNeuve } from '../pages/MaisonNeuve';
import { Renovation } from '../pages/Renovation';
import { Photovoltaique } from '../pages/Photovoltaique';
import { Climatisation } from '../pages/Climatisation';
import { Service } from '../types';

interface ServiceModalProps {
  isOpen: boolean;
  serviceId: string;
  onClose: () => void;
  onSwitch: (id: string) => void;
  services: Service[];
}

export const ServiceModal: React.FC<ServiceModalProps> = ({ isOpen, serviceId, onClose, onSwitch, services }) => {
  const currentServiceIndex = services.findIndex(s => s.id === serviceId);
  const nextService = services[(currentServiceIndex + 1) % services.length];
  const prevService = services[(currentServiceIndex - 1 + services.length) % services.length];

  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';

      // Add keyboard navigation support
      const handleKeyDown = (e: KeyboardEvent) => {
        if (e.key === 'ArrowRight') onSwitch(nextService.id);
        if (e.key === 'ArrowLeft') onSwitch(prevService.id);
        if (e.key === 'Escape') onClose();
      };
      window.addEventListener('keydown', handleKeyDown);
      return () => {
        document.body.style.overflow = 'auto';
        window.removeEventListener('keydown', handleKeyDown);
      };
    } else {
      document.body.style.overflow = 'auto';
    }
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isOpen, nextService.id, prevService.id, onSwitch, onClose]);

  if (!isOpen) return null;

  const renderContent = () => {
    switch (serviceId) {
      case 'maison-neuve': return <MaisonNeuve />;
      case 'renovation': return <Renovation />;
      case 'photovoltaique': return <Photovoltaique />;
      case 'clim': return <Climatisation />;
      default: return <MaisonNeuve />;
    }
  };

  const scrollToTop = () => {
    const scrollContainer = document.querySelector('.modal-scroll-container');
    if(scrollContainer) scrollContainer.scrollTop = 0;
  };

  return (
    <div className="fixed inset-0 z-[60] bg-white dark:bg-neutral-950 flex flex-col animate-fade-in">
      {/* Modal Header */}
      <div className="absolute top-0 left-0 w-full h-20 bg-white/90 dark:bg-neutral-950/90 backdrop-blur-md z-50 flex items-center justify-between px-4 border-b border-gray-100 dark:border-neutral-800 shadow-sm">
        <div className="flex items-center">
            <span className="font-display font-bold text-primary dark:text-white text-lg">
                TM BTP <span className="text-accent text-sm ml-2 font-normal hidden sm:inline">Expertise</span>
            </span>
        </div>

        {/* Navigation Controls */}
        <div className="flex items-center gap-2">
          <div className="flex items-center bg-gray-100 dark:bg-neutral-800 rounded-full p-1 mr-2">
            <button 
              onClick={() => { onSwitch(prevService.id); scrollToTop(); }}
              className="w-8 h-8 flex items-center justify-center rounded-full hover:bg-white dark:hover:bg-neutral-700 text-primary dark:text-white transition-all"
              title="Précédent"
            >
              <Icons.ChevronRight className="w-4 h-4 transform rotate-180" />
            </button>
            <div className="w-px h-4 bg-gray-300 dark:bg-neutral-700 mx-1"></div>
            <button 
              onClick={() => { onSwitch(nextService.id); scrollToTop(); }}
              className="w-8 h-8 flex items-center justify-center rounded-full hover:bg-white dark:hover:bg-neutral-700 text-primary dark:text-white transition-all"
              title="Suivant"
            >
              <Icons.ChevronRight className="w-4 h-4" />
            </button>
          </div>

          <button 
            onClick={onClose}
            className="w-10 h-10 bg-gray-100 dark:bg-neutral-800 rounded-full flex items-center justify-center text-primary dark:text-white hover:bg-red-50 hover:text-red-500 dark:hover:bg-red-900/20 dark:hover:text-red-400 transition-colors"
            title="Fermer"
          >
            <Icons.Close className="w-5 h-5" />
          </button>
        </div>
      </div>

      {/* Content Container (Scrollable) */}
      <div className="flex-grow overflow-y-auto overflow-x-hidden bg-white dark:bg-neutral-950 relative modal-scroll-container">
        {/* We use a negative margin top to pull the content up under the transparent header if needed, 
            but pages have pt-20. Since our header is fixed, pt-20 works perfectly to spacing. */}
        <div className="min-h-screen">
            {renderContent()}
        </div>

        {/* Footer spacer */}
        <div className="h-24"></div>
      </div>

      {/* Floating Bottom Nav (Mobile Only) */}
      <div className="absolute bottom-6 left-0 w-full z-50 px-4 flex justify-center pointer-events-none md:hidden">
        <div className="bg-primary/90 dark:bg-neutral-800/90 backdrop-blur-md text-white px-6 py-3 rounded-full shadow-2xl flex items-center gap-4 pointer-events-auto border border-white/10 max-w-sm w-full justify-between">
           <span className="text-xs font-medium text-gray-300 uppercase tracking-wider">Expertise suivante</span>
           <button 
             onClick={() => {
                scrollToTop();
                onSwitch(nextService.id);
             }}
             className="flex items-center font-bold text-sm hover:text-accent transition-colors"
           >
              {nextService.title}
              <Icons.ChevronRight className="w-4 h-4 ml-1" />
           </button>
        </div>
      </div>
    </div>
  );
};

