        </div>
      </div>
    </nav>
  );
};

import React, { useEffect, useRef, useState } from 'react';

declare global {
  interface Window {
    google: any;
    initMap: () => void;
  }
}

export const InteractiveAreaMap: React.FC = () => {
  const mapRef = useRef<HTMLDivElement>(null);
  const [mapError, setMapError] = useState<string | null>(null);

  useEffect(() => {
    // Prevent multiple script injections
    if (window.google && window.google.maps) {
      initializeMap();
      return;
    }

    const scriptId = 'google-maps-script';
    if (document.getElementById(scriptId)) return;

    window.initMap = () => {
      initializeMap();
    };

    const script = document.createElement('script');
    script.id = scriptId;
    script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.API_KEY}&callback=initMap`;
    script.async = true;
    script.defer = true;
    script.onerror = () => setMapError("Erreur de chargement de la carte");
    document.head.appendChild(script);

    return () => {
      // Cleanup if necessary
      delete (window as any).initMap;
    };
  }, []);

  const initializeMap = () => {
    if (!mapRef.current || !window.google) return;

    const toulouse = { lat: 43.6047, lng: 1.4442 };
    const tarbes = { lat: 43.2333, lng: 0.0833 };
    const montauban = { lat: 44.0167, lng: 1.35 };

    const isDarkMode = document.documentElement.classList.contains('dark');

    const mapStyles = isDarkMode ? [
      { elementType: "geometry", stylers: [{ color: "#242f3e" }] },
      { elementType: "labels.text.stroke", stylers: [{ color: "#242f3e" }] },
      { elementType: "labels.text.fill", stylers: [{ color: "#746855" }] },
      {
        featureType: "administrative.locality",
        elementType: "labels.text.fill",
        stylers: [{ color: "#d59563" }],
      },
      {
        featureType: "poi",
        elementType: "labels.text.fill",
        stylers: [{ color: "#d59563" }],
      },
      {
        featureType: "road",
        elementType: "geometry",
        stylers: [{ color: "#38414e" }],
      },
      {
        featureType: "road",
        elementType: "geometry.stroke",
        stylers: [{ color: "#212a37" }],
      },
      {
        featureType: "road",
        elementType: "labels.text.fill",
        stylers: [{ color: "#9ca5b3" }],
      },
      {
        featureType: "water",
        elementType: "geometry",
        stylers: [{ color: "#17263c" }],
      },
    ] : [];

    const map = new window.google.maps.Map(mapRef.current, {
      center: toulouse,
      zoom: 8,
      styles: mapStyles,
      disableDefaultUI: true,
      zoomControl: true,
      mapId: 'DEMO_MAP_ID', // Optional but recommended for newer features
    });

    // Simple Markers
    const createMarker = (position: {lat: number, lng: number}, title: string) => {
      new window.google.maps.Marker({
        position,
        map,
        title,
        animation: window.google.maps.Animation.DROP,
      });
    };

    createMarker(tarbes, "TM BTP - Tarbes");
    createMarker(montauban, "TM BTP - Montauban");

    // Service Area Circle centered on Toulouse
    new window.google.maps.Circle({
      strokeColor: "#C9A227",
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: "#C9A227",
      fillOpacity: 0.1,
      map,
      center: toulouse,
      radius: 80000, // 80km radius covers Tarbes and Montauban well from Toulouse
    });
  };

  return (
    <div className="w-full h-[250px] md:h-[350px] bg-neutral-100 dark:bg-neutral-800 relative overflow-hidden rounded-xl border border-gray-200 dark:border-neutral-700 shadow-inner group">
      {mapError ? (
        <div className="absolute inset-0 flex items-center justify-center p-6 text-center">
          <p className="text-gray-400 text-sm font-medium">{mapError}</p>
        </div>
      ) : (
        <div ref={mapRef} className="w-full h-full" />
      )}

      {/* Small floating indicator */}
      <div className="absolute bottom-4 left-4 z-10 pointer-events-none">
        <div className="bg-white/90 dark:bg-neutral-900/90 backdrop-blur-md px-3 py-1.5 rounded-full border border-gray-200 dark:border-neutral-700 shadow-lg flex items-center gap-2">
            <span className="w-2 h-2 rounded-full bg-accent animate-pulse"></span>
            <span className="text-[10px] font-bold text-primary dark:text-white uppercase tracking-wider">Rayon d'action : 80km</span>
        </div>
      </div>
    </div>
  );
};

import React, { useEffect, useRef, useState } from 'react';

declare global {
  interface Window {
    google: any;
    initMap: () => void;
    gm_authFailure: () => void;
  }
}

export const InteractiveAreaMap: React.FC = () => {
  const mapRef = useRef<HTMLDivElement>(null);
  const [mapStatus, setMapStatus] = useState<'loading' | 'ready' | 'error'>('loading');
  const [errorMsg, setErrorMsg] = useState<string>("");

  useEffect(() => {
    // Handle Google Maps authentication failures (InvalidKeyMapError, etc.)
    window.gm_authFailure = () => {
      console.error("Google Maps Authentication Failed");
      setMapStatus('error');
      setErrorMsg("La clé API Google Maps semble invalide ou n'est pas configurée pour ce domaine.");
    };

    const initializeMap = () => {
      if (!mapRef.current || !window.google) return;

      try {
        const toulouse = { lat: 43.6047, lng: 1.4442 };
        const tarbes = { lat: 43.2333, lng: 0.0833 };
        const montauban = { lat: 44.0167, lng: 1.35 };

        const isDarkMode = document.documentElement.classList.contains('dark');

        const mapStyles = isDarkMode ? [
          { elementType: "geometry", stylers: [{ color: "#242f3e" }] },
          { elementType: "labels.text.stroke", stylers: [{ color: "#242f3e" }] },
          { elementType: "labels.text.fill", stylers: [{ color: "#746855" }] },
          { featureType: "water", elementType: "geometry", stylers: [{ color: "#17263c" }] },
          { featureType: "road", elementType: "geometry", stylers: [{ color: "#38414e" }] }
        ] : [];

        const map = new window.google.maps.Map(mapRef.current, {
          center: toulouse,
          zoom: 8,
          styles: mapStyles,
          disableDefaultUI: true,
          zoomControl: true,
          gestureHandling: "cooperative"
        });

        // Simple Markers
        new window.google.maps.Marker({ position: tarbes, map, title: "TM BTP - Tarbes" });
        new window.google.maps.Marker({ position: montauban, map, title: "TM BTP - Montauban" });

        // Service Area Circle
        new window.google.maps.Circle({
          strokeColor: "#C9A227",
          strokeOpacity: 0.8,
          strokeWeight: 2,
          fillColor: "#C9A227",
          fillOpacity: 0.1,
          map,
          center: toulouse,
          radius: 85000, // 85km around Toulouse
        });

        setMapStatus('ready');
      } catch (err) {
        setMapStatus('error');
        setErrorMsg("Impossible d'initialiser la carte interactive.");
      }
    };

    if (window.google && window.google.maps) {
      initializeMap();
    } else {
      window.initMap = initializeMap;
      const scriptId = 'google-maps-js';
      if (!document.getElementById(scriptId)) {
        const script = document.createElement('script');
        script.id = scriptId;
        script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.API_KEY}&callback=initMap`;
        script.async = true;
        script.defer = true;
        script.onerror = () => {
          setMapStatus('error');
          setErrorMsg("Erreur réseau lors du chargement de Google Maps.");
        };
        document.head.appendChild(script);
      }
    }

    return () => {
      // Cleanup to avoid memory leaks or multiple declarations
      if (window.initMap) delete (window as any).initMap;
    };
  }, []);

  return (
    <div className="w-full h-[300px] md:h-[350px] bg-neutral-100 dark:bg-neutral-800 relative overflow-hidden rounded-xl border border-gray-200 dark:border-neutral-800 shadow-inner group">

      {/* Loading State */}
      {mapStatus === 'loading' && (
        <div className="absolute inset-0 flex flex-col items-center justify-center bg-neutral-50 dark:bg-neutral-900 z-20">
          <div className="w-8 h-8 border-4 border-accent border-t-transparent rounded-full animate-spin mb-4"></div>
          <p className="text-xs font-bold text-gray-400 uppercase tracking-widest">Chargement de la zone...</p>
        </div>
      )}

      {/* Error / Fallback State */}
      {mapStatus === 'error' && (
        <div className="absolute inset-0 flex flex-col items-center justify-center bg-neutral-50 dark:bg-neutral-900 p-8 text-center z-20">
          <div className="w-12 h-12 bg-red-50 dark:bg-red-900/20 rounded-full flex items-center justify-center mb-4">
             <span className="text-red-500 text-xl">!</span>
          </div>
          <h4 className="text-primary dark:text-white font-bold mb-2">Carte Indisponible</h4>
          <p className="text-gray-500 dark:text-gray-400 text-xs max-w-xs mx-auto leading-relaxed mb-4">
            {errorMsg || "La carte n'a pas pu être chargée correctement."}
          </p>
          <div className="px-4 py-2 bg-accent/10 border border-accent/20 rounded-lg">
             <p className="text-accent-dark dark:text-accent text-[10px] font-bold uppercase">Zone couverte : Rayon de 80km autour de Toulouse</p>
          </div>
        </div>
      )}

      {/* Map Canvas */}
      <div ref={mapRef} className={`w-full h-full transition-opacity duration-700 ${mapStatus === 'ready' ? 'opacity-100' : 'opacity-0'}`} />

      {/* Fixed Indicator Overlay */}
      {mapStatus === 'ready' && (
        <div className="absolute bottom-4 left-4 z-10 pointer-events-none">
          <div className="bg-white/95 dark:bg-neutral-900/95 backdrop-blur-md px-3 py-1.5 rounded-full border border-gray-200 dark:border-neutral-700 shadow-lg flex items-center gap-2">
              <span className="w-2 h-2 rounded-full bg-accent animate-pulse"></span>
              <span className="text-[10px] font-bold text-primary dark:text-white uppercase tracking-wider">Intervention : Haute-Garonne, Hautes-Pyrénées, Tarn-et-Garonne</span>
          </div>
        </div>
      )}
    </div>
  );
};

import React, { useEffect, useRef } from 'react';

declare global {
  interface Window {
    L: any;
  }
}

export const InteractiveAreaMap: React.FC = () => {
  const mapContainerRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);

  useEffect(() => {
    if (!mapContainerRef.current || !window.L) return;

    // Center on Toulouse
    const toulouse: [number, number] = [43.6047, 1.4442];
    const tarbes: [number, number] = [43.2333, 0.0833];
    const montauban: [number, number] = [44.0167, 1.35];

    // Detect theme
    const isDarkMode = document.documentElement.classList.contains('dark');

    // Initialize Map
    mapInstanceRef.current = window.L.map(mapContainerRef.current, {
      center: toulouse,
      zoom: 8,
      zoomControl: false,
      scrollWheelZoom: false,
      dragging: !window.L.Browser.mobile,
      tap: !window.L.Browser.mobile,
    });

    // Tiles selection based on theme
    const tileUrl = isDarkMode 
      ? 'https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png'
      : 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png';

    window.L.tileLayer(tileUrl, {
      attribution: '&copy; CartoDB',
      subdomains: 'abcd',
      maxZoom: 19
    }).addTo(mapInstanceRef.current);

    // Custom Marker Icon
    const customIcon = window.L.divIcon({
      className: 'custom-div-icon',
      html: `<div class="w-4 h-4 bg-accent border-2 border-white dark:border-neutral-900 rounded-full shadow-lg pulse-marker"></div>`,
      iconSize: [16, 16],
      iconAnchor: [8, 8]
    });

    // Add Markers
    window.L.marker(tarbes, { icon: customIcon }).addTo(mapInstanceRef.current).bindPopup('<b>TM BTP Tarbes</b>');
    window.L.marker(montauban, { icon: customIcon }).addTo(mapInstanceRef.current).bindPopup('<b>TM BTP Montauban</b>');

    // Service Area Circle
    window.L.circle(toulouse, {
      color: '#C9A227',
      fillColor: '#C9A227',
      fillOpacity: 0.1,
      radius: 85000, // 85km radius
      weight: 2,
      dashArray: '5, 10'
    }).addTo(mapInstanceRef.current);

    // Style for marker pulse
    const style = document.createElement('style');
    style.innerHTML = `
      .pulse-marker {
        box-shadow: 0 0 0 rgba(201, 162, 39, 0.4);
        animation: pulse 2s infinite;
      }
      @keyframes pulse {
        0% { box-shadow: 0 0 0 0 rgba(201, 162, 39, 0.7); }
        70% { box-shadow: 0 0 0 10px rgba(201, 162, 39, 0); }
        100% { box-shadow: 0 0 0 0 rgba(201, 162, 39, 0); }
      }
    `;
    document.head.appendChild(style);

    return () => {
      if (mapInstanceRef.current) {
        mapInstanceRef.current.remove();
        mapInstanceRef.current = null;
      }
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div className="w-full h-[300px] md:h-[350px] bg-neutral-100 dark:bg-neutral-800 relative overflow-hidden rounded-xl border border-gray-200 dark:border-neutral-800 shadow-inner group">
      <div ref={mapContainerRef} className="w-full h-full z-0" />

      {/* Indicator Overlay */}
      <div className="absolute bottom-4 left-4 z-[1000] pointer-events-none">
        <div className="bg-white/95 dark:bg-neutral-900/95 backdrop-blur-md px-3 py-1.5 rounded-full border border-gray-200 dark:border-neutral-700 shadow-lg flex items-center gap-2">
            <span className="w-2 h-2 rounded-full bg-accent animate-pulse"></span>
            <span className="text-[10px] font-bold text-primary dark:text-white uppercase tracking-wider">Zone Sud-Ouest : Rayon 85km autour de Toulouse</span>
        </div>
      </div>
    </div>
  );
};

